mapper.insert:
insert into msg_list (userId,anotherId,content,unread,time) values (#{userId},#{anotherId},#{content},1,CURRENT_TIMESTAMP) on duplicate key update content=#{content},unread=unread+1,time=CURRENT_TIMESTAMP;
insert into msg_list (userId,anotherId,content,unread,time) values (#{anotherId},#{userId},#{content},1,CURRENT_TIMESTAMP) on duplicate key update content=#{content},unread=unread+1,time=CURRENT_TIMESTAMP;
insert into messages (userId,anotherId,content,time) values(#{userId},#{anotherId},#{content},CURRENT_TIMESTAMP);
